// More - Less 
$(function () {
    /* Scroll function required for More/Less on click event */
    function scrollToDiv(element, navheight) {
        var offset = element.offset();
        var offsetTop = offset.top;
        var totalScroll = offsetTop - navheight;
        $('body,html').animate({
            scrollTop: totalScroll
        }, 500);
    }
    /* Expand / hide sections */
    $('.more-link').on('click', 'a.expand', function (e) {
        e.preventDefault();
        $(this).parents('.more-link').siblings('.hidden-post').slideToggle();
        $(this).toggleClass('expanded');
    });
    $('.artists .more-link').on('click', 'a', function (e) {
        if ($(this).hasClass('expanded')) {
            $(this).text('Fewer artists');
        } else {
            $(this).text('More artists');
        }
    });
    $('.events .more-link').on('click', 'a', function (e) {
        if ($(this).hasClass('expanded')) {
            $(this).text('Fewer events');
        } else {
            $(this).text('More events');
        }
    });
});


